  }

  return (
    <div>
      <BrowserRouter>
        <Routes>
          <Route path = "/Home" element = { <CardContainer data = { formData }/> } />
          <Route path = "/Signup" element = { <Signup/> } />
          <Route path = "/Login" element = { <Login handleLogin = { handleLogin }/> } />
          <Route path = "*" element = { <Navigate to = "Home" replace /> } />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
